
public class Conversion {
//конвертация дробных чисел в byte
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//int z=0; 
		byte a;
		double x;
		for (x=0;x<1001;x++) {
			a= (byte) x;
			System.out.print("double x= "+x);
			System.out.println(";  byte a= "+a);
		}
	}
}

	


